unitTests.test:
        
var addressArg = new blackberry.invoke.AddressBookArguments();
var calendarArg = new blackberry.invoke.CalendarArguments();
var mapsArg = new blackberry.invoke.MapsArguments();
var memoArg = new blackberry.invoke.MemoArguments();
var messageArg = new blackberry.invoke.MessageArguments();
var phoneArg = new blackberry.invoke.PhoneArguments();
var searchArg = new blackberry.invoke.SearchArguments();
var taskArg = new blackberry.invoke.TaskArguments();
var browserArg = new blackberry.invoke.BrowserArguments('http://www.rim.com');
var cameraArg = new blackberry.invoke.CameraArguments();
var javaArg = new blackberry.invoke.JavaArguments('net_rim_bb_memo_app');



//exceptions

 var unexpectedArgError = "java.lang.IllegalArgumentException: Required argument missing";
 var nullException = "java.lang.IllegalArgumentException: Argument is not nullable";
 var invalidTypeException = "java.lang.IllegalArgumentException: Invalid type: 'args'";
 
 var extraArgException = "java.lang.IllegalArgumentException: Too many arguments";
 var nonStringException = "java.lang.IllegalArgumentException: Invalid type - class java.lang.String";
     

var extraNULLException ="java.lang.IllegalArgumentException: Function not found with corresponding number of arguments";
var extraArgException2 = "java.lang.IllegalArgumentException: Function not found with corresponding number of arguments";   // need to be consistent here
function invoke_Exception()
{
    
    
    try {
        blackberry.invoke.invoke();
        throw('DPI224702: Invoke with no arguments should throw exception');
    } catch (err) {
        if ( err != unexpectedArgError ) {
            throw('Unexpected Error when calling invoke with no arguments: '+err);
        }
    } 

    try {
        blackberry.invoke.invoke(null);
        throw('DPI224702: Invoke with null arguments should throw exception');
    } catch (err) {
        if ( err != nullException ) {
            throw('Unexpected Error when calling invoke with null arguments: '+err);
        }
    }    

    try {
        blackberry.invoke.invoke(undefined);
        throw('DPI224702: Invoke with undefined arguments should throw exception');
    } catch (err) {
        if ( err != nullException ) {
            throw('Unexpected Error when calling invoke with undefined arguments: '+err);
        }
    }

    try {
        blackberry.invoke.invoke('TestType', addressArg);
        throw('DPI224702: Invoke with string as appType should throw exception');
    } catch (err) {
        if ( err != nonStringException ) {
            throw('Unexpected Error when calling invoke with string appType: '+err);
        }
    }

    try {
        blackberry.invoke.invoke(-1, addressArg);
        throw('DPI224702: Invoke with -1 as appType should throw exception');
    } catch (err) {
        if ( err != "java.lang.IllegalArgumentException: Invalid 'appType'") {
            throw('Unexpected Error when calling invoke with -1 as appType: '+err);
        }
    }

    try {
        blackberry.invoke.invoke(13, addressArg);
        throw('DPI224702: Invoke with 13 as appType should throw exception');
    } catch (err) {
        if ( err != "java.lang.IllegalArgumentException: Invalid 'appType'" ) {
            throw('Unexpected Error when calling invoke with 13 as appType: '+err);
        }
    }

return true;

}

        

function invoke_AddressBook_Exception()
{
    

    try {
        blackberry.invoke.invoke(blackberry.invoke.APP_ADDRESSBOOK, 12);
        throw('DPI224702: Invoke with int args should throw exception');
    } catch (err) {
        if ( err !=  invalidTypeException) {
            throw('Unexpected Error when calling invoke with int args: '+err);
        }
    } 

    try {
        blackberry.invoke.invoke(blackberry.invoke.APP_ADDRESSBOOK, 'TestBook');
        throw('DPI224702: Invoke with string args should throw exception');
    } catch (err) {
        if ( err != invalidTypeException) {
            throw('Unexpected Error when calling invoke with string args: '+err);
        }
    } 

    try {
        blackberry.invoke.invoke(blackberry.invoke.APP_ADDRESSBOOK, addressArg, null);
        throw('DPI224702: Invoke with extra null args should throw exception');
    } catch (err) {
        if ( err != extraNULLException ) {
            throw('Unexpected Error when calling invoke with extra null args: '+err);
        }
    } 

    try {
        blackberry.invoke.invoke(blackberry.invoke.APP_ADDRESSBOOK, addressArg, undefined);
        throw('DPI224702: Invoke with extra undefined args should throw exception');
    } catch (err) {
        if ( err != extraNULLException ) {
            throw('Unexpected Error when calling invoke with extra undefined args: '+err);
        }
    } 

    try {
        blackberry.invoke.invoke(blackberry.invoke.APP_ADDRESSBOOK, addressArg, 12);
        throw('DPI224702: Invoke with extra int args should throw exception');
    } catch (err) {
        if ( err != extraArgException2 ) {
            throw('Unexpected Error when calling invoke with extra int args: '+err);
        }
    } 

    try {
        blackberry.invoke.invoke(blackberry.invoke.APP_ADDRESSBOOK, addressArg, 'Test');
        throw('DPI224702: Invoke with extra string args should throw exception');
    } catch (err) {
        if ( err != extraArgException2 ) {
            throw('Unexpected Error when calling invoke with extra string args: '+err);
        }
    } 
     
