        throw('DPI224737: Invoke with Maps Arg should throw exception');
    } catch (err) {
        if ( err != invalidTypeException ) {
            throw('Unexpected Error when calling invoke with Maps Arg: '+err);
        }
    } 

    try {
        blackberry.invoke.invoke(blackberry.invoke.APP_ADDRESSBOOK, messageArg);
        throw('DPI224737: Invoke with Message Arg should throw exception');
    } catch (err) {
        if ( err != invalidTypeException ) {
            throw('Unexpected Error when calling invoke with Message Arg: '+err);
        }
    } 

    try {
        blackberry.invoke.invoke(blackberry.invoke.APP_ADDRESSBOOK, phoneArg);
        throw('DPI224737: Invoke with Phone Arg should throw exception');
    } catch (err) {
        if ( err !=invalidTypeException ) {
            throw('Unexpected Error when calling invoke with Phone Arg: '+err);
        }
    } 

    try {
        blackberry.invoke.invoke(blackberry.invoke.APP_ADDRESSBOOK, searchArg);
        throw('DPI224737: Invoke with Search Arg should throw exception');
    } catch (err) {
        if ( err != invalidTypeException ) {
            throw('Unexpected Error when calling invoke with Search Arg: '+err);
        }
    } 

    try {
